import React, { useEffect, useState } from 'react';
import Navbar from '../Navbar/Navbar';
import './css/style.css'
import './css/HomeMobile.css';
import { cate, products } from './products';
import Header from './Header';
import { Link } from 'react-router-dom';
function Home(props) {
    const [curr, setCurr] = useState(0)
    const locate = (id,pic) => {
        localStorage.setItem("item",JSON.stringify({id:id,img:pic}))
    }
    var i = 0;
    useEffect(() => {
        var i = 0;
        setInterval(() => {
            i++;
            if (i > 6) {
                i = 0
                setCurr(i)
            } else {
                setCurr(i)
            }
        }, 3000);
    }, [i])
    const rand = () => {
        return arr[Math.floor((Math.random() * arr.length))]
    }
    let path = process.env.PUBLIC_URL + '/images/'
    let arr = ['yes.png', 'shop.jpg', 'wl3.jpg', 'wl2.jpg', 'wl.jpg', 'diff.jpg', 'inovate.jpg']
    return (
        <div className='body' id="n">

            <div className='darken' style={{ backgroundImage: `url(${path + 'shoebg.jpg'})` }}></div>
            <Navbar />
            <Header />
            <main className='bg-light'>
                <div className='xyz'>
                    <div className='border aaa'>
                        <div className="a-head">Categories</div>
                        <div className="cate">
                            {cate.map(({ sex, link }) => {
                                return (<div className="eachcate">
                                    {sex}
                                </div>)
                            })}
                        </div>

                    </div>
                    <div className="bbb position-relative" style={{ backgroundImage: `url("${path + arr[curr]}")` }}>
                        <i className='fa fa-arrow-left leftarrow arr'></i>
                        <i className='fa fa-arrow-right rightarrow arr'></i>
                        <div><i className='fa fa-circle'></i><i className='fa fa-circle'></i></div>
                    </div>
                    <div className="eee">
                        <span style={{ color: "green" }}>Q</span>uick
                        <span style={{ color: "black" }}> D</span>elivery
                    </div>
                    <div className='product bg-light'>
                        {products.map(({ name, img, desc, price }, index) => {
                            return (
                                <div className="h-auto item" onClick={() => { locate(index,rand()) }}>
                                    <Link to={"/item?id=" + index + "?srcpathquery?=" + rand()} >
                                        <img src={path + rand()} alt="" width="100%" height="70%" />
                                        <div className="desc text-dark" style={{ height: "30%" }}>
                                            <span className='name'>{name}</span> <br />
                                            <span className="price"><span>&#x20A6;</span>{price}</span>
                                        </div>
                                    </Link>
                                </div>
                            )
                        })}
                    </div>
                    <div className="ddd" >
                        <div className='ddd1'>
                        </div>
                        <div className='ddd2'>
                        </div>
                    </div>
                </div>
            </main>
            <footer className='position-relative text-light'>
                <span className="position-absolute">Â© copyright kennyCode</span>
            </footer>
        </div>
    );
}
export default Home;
